INSERT INTO `t_scenery` VALUES ('c730fc01-7a72-4622-b979-fa72c6a991b8', '美国纽约', '纽约州', '纽约', '美国', '5A', '人工建成', '少儿 青年 老年', '50000', '纽约（New York），是纽约都会区的核心，也是美国最大城市。为了与其所在的纽约州相区分，被称为纽约市。纽约位于美国东海岸的东北部，是美国人口最多的城市，也是个多族裔聚居的多元化城市。', NULL);
